{
    "core_beliefs": [
        {
            "core_belief_type": "Unlovable Core Beliefs",
            "explanation": "The client's experiences of being questioned about her identity and qualifications, coupled with her sensitivity to perceived criticism, suggest a core belief related to unlovability. She seems to fear being seen as 'not enough' or 'not truly belonging,' leading to defensiveness and a need to prove herself.",
            "client_core_belief": [
                "I am not good enough [in terms of achievement].",
                "I am different."
            ]
        }
    ],
    "intermediate_beliefs": [
        {
            "intermediate_belief": "If I work hard and achieve success, people will accept and value me.",
            "positive_assumption": "If I am successful in my career, people will see my worth and accept me.",
            "negative_assumption": "If I am not successful, people will reject me and see me as an outsider."
        },
        {
            "intermediate_belief": "I must always be prepared to defend my identity and qualifications.",
            "positive_assumption": "If I am well-prepared and articulate, I can overcome others' biases and misunderstandings.",
            "negative_assumption": "If I am caught off guard or unable to defend myself, I will be seen as inadequate or not belonging."
        },
        {
            "intermediate_belief": "I must mask my anger to be accepted.",
            "positive_assumption": "If I control my anger, people will like me.",
            "negative_assumption": "If I show anger, people will reject me."
        }
    ]
}
